import React, { useState } from "react";

function Loading({ children }) {
  const [dots, setDots] = useState("...");
  let interval = setInterval(() => {
    if (dots.length >= 3) setDots(".");
    else setDots(dots + ".");
  }, 300);
  return <div>{children ? children : `Loading${dots}`}</div>;
}

export default Loading;
